import React, { useContext, useEffect } from 'react';
import cn from 'classnames';
import { BaseModalContext } from '../../../../base-modal/modern';

var styles = {"footer":"bottom-sheet__footer_tpuez","highlighted":"bottom-sheet__highlighted_tpuez"};
require('./index.css');

const Footer = ({ children, className }) => {
    const { footerHighlighted, setHasFooter } = useContext(BaseModalContext);
    useEffect(() => {
        setHasFooter(true);
    }, [setHasFooter]);
    return (React.createElement("div", { className: cn(styles.footer, className, {
            [styles.highlighted]: footerHighlighted,
        }) }, children));
};

export { Footer };
