const getPath = ({ size, hasTopAddons, hasBottomAddons, pathsMap, }) => {
    if (hasBottomAddons && hasTopAddons) {
        return pathsMap[size].topBottom;
    }
    if (hasBottomAddons) {
        return pathsMap[size].bottom;
    }
    if (hasTopAddons) {
        return pathsMap[size].top;
    }
    return pathsMap[size].none;
};

export { getPath };
