import React, { useEffect } from 'react';
import cn from 'classnames';
import computeScrollIntoView from 'compute-scroll-into-view';

var styles = {"container":"tabs__container_1ak8z"};
require('./index.css');

const ScrollableContainer = ({ containerClassName, children, activeChild, }) => {
    useEffect(() => {
        if (activeChild) {
            const actions = computeScrollIntoView(activeChild, {
                scrollMode: 'if-needed',
                block: 'nearest',
                inline: 'nearest',
            });
            // TODO: animate?
            actions.forEach(({ el, left }) => {
                // eslint-disable-next-line no-param-reassign
                el.scrollLeft = left;
            });
        }
    }, [activeChild]);
    return React.createElement("div", { className: cn(styles.container, containerClassName) }, children);
};

export { ScrollableContainer };
